const downloadProfileImage = (arr, index, err) => {
    currentImageIndex = currentImageIndex + 1;
    if (arr[index].imagePortraitUrl != null) {
        const file = fs.createWriteStream("public/imagePortrait/" + arr[index].name.replace(/\s+/g, '-').toLowerCase() + ".png");
        const request = https.get(arr[index].imagePortraitUrl, function (response) {
            file.on('finish', function () {
                console.log(index + ', ' + arr[index].name + ' ,profile image downloaded');
                index == lastIndex ? downloadWallOfLeetImage(arr, 0, false) :
                    err ? null : downloadProfileImage(arr, index + 1, false);
                
                
            })
            response.pipe(file);
            request.on('error', (e) => {
                    console.log(e)
                    downloadProfileImage(arr, index, true)
                })
        });
    } else {
        index == lastIndex?downloadWallOfLeetImage(arr,0,false):downloadProfileImage(arr,index+1,false)
    }
}
const downloadWallOfLeetImage = (arr, index, err) => {
    currentImageIndex = currentImageIndex + 1;
    if (arr[index].imageWallOfLeetUrl != null) {
        const file = fs.createWriteStream("public/imageWallOfLeet/" + arr[index].name.replace(/\s+/g, '-').toLowerCase() + ".png");
        const request = https.get(arr[index].imageWallOfLeetUrl, function (response) {
            file.on('finish', function () {
                console.log(index + ', ' + arr[index].name + ' ,wall of leet image downloaded');
                index == lastIndex ? null :
                    err ? null : downloadWallOfLeetImage(arr, index + 1, false);
                
                })
            response.pipe(file);
            request.on('error', (e) => {
                console.log(e)
                downloadWallOfLeetImage(arr, index, true)
                })
              });
    } else {
        index == lastIndex?null:downloadWallOfLeetImage(arr,index+1, false)
    }
      
}

