import React, { useLayoutEffect, useState } from 'react';

const Layout = props => {
    const { profiles } = props
    let numberOfRows = Math.floor(useWindowSize()[1] * 0.9 / (160))
    let numberOfContainers = Math.floor(useWindowSize()[0] * 0.9 / (290))
    num
    let columns = []
    for (let i = 0; i < numberOfRows; i++){
        columns.push(profiles.slice(i*numberOfContainers, i*numberOfContainers+numberOfContainers))
    }
    return (
        <div style={{ width: "90%",height:"80%", background: "green", margin: "auto", padding: "20px" }}>
            {columns.map(value => {
                return <Row profiles={value}></Row>
            })}     
        </div>
    )
}


const Row = props => {
    const {profiles}=props
    return (
        <div style={{ display: "flex", flexDirection: "row", justifyContent: "space-around", alignItems: "center" }}>
            <div style={{ display: "flex", flexDirection: "row" }}>
                {profiles.map(value => {
                    return ProfileContainer(value)
                })}
            </div>
        </div>
    )
}

const ProfileContainer = (testValue) => {
    return (
        <div style={{ width: "280px", height: "140px", background: "white", margin: "10px" }}>{testValue}</div>
    )
}

const useWindowSize=()=> {
  const [size, setSize] = useState([0, 0]);
  useLayoutEffect(() => {
    const updateSize=()=> {
      setSize([window.innerWidth, window.innerHeight]);
    }
    window.addEventListener('resize', updateSize);
    updateSize();
    return () => window.removeEventListener('resize', updateSize);
  }, []);
  return size;
}

export default Layout
