import React, { useLayoutEffect, useState } from 'react';

const Layout = props => {
    const {profiles} = props
    return (
        <div style={{width:"90%", background:"green", margin: "auto", padding:"20px"}}><Row profiles={profiles}></Row></div>
    )
}

const Row = props => {
    const {profiles}=props
    console.log(profiles)
    return (
        <div>
            {RenderProfielContainers()}
            {/* <ShowWindowDimensions></ShowWindowDimensions>
            <ShowWindowDimensions></ShowWindowDimensions>
            <ShowWindowDimensions></ShowWindowDimensions> */}
        </div>
    )
}

const RenderProfielContainers = () => {
    let windowDimensions = useWindowSize()
    let numberOfContainers = Math.floor(windowDimensions[0] * 0.9 / (220))
    return numberOfContainers
}

const ProfileContainer = () => {
    return (
        <div style={{width:"200px", height:"150px", background:"white", margin:"20px"}}></div>
    )
}

const useWindowSize=()=> {
  const [size, setSize] = useState([0, 0]);
  useLayoutEffect(() => {
    const updateSize=()=> {
      setSize([window.innerWidth, window.innerHeight]);
    }
    window.addEventListener('resize', updateSize);
    updateSize();
    return () => window.removeEventListener('resize', updateSize);
  }, []);

  return size;
}

const ShowWindowDimensions=(props)=> {
    const [width, height] = useWindowSize();
    const numberOfContainers = RenderProfielContainers()
    return <span>Window size: {width} x {height}, number of containers in a row { numberOfContainers}</span>;
}

export default Layout
