import React, { useLayoutEffect, useState } from 'react';

const Layout = props => {
    const {profiles} = props
    return (
        <div style={{ width: "90%", background: "green", margin: "auto", padding: "20px" }}>
            <Row profiles={profiles}></Row>
        </div>
    )
}

const Column = props => {
    const {profiles}=props
    let numberOfRows = Math.floor(useWindowSize()[1] * 0.9 / (290))
    let rowProfiles = profiles.slice(0,numberOfRows)
    return (
        <div style={{ display: "flex", flexDirection: "row", justifyContent: "space-around", alignItems: "center" }}>
            <div style={{ display: "flex", flexDirection: "row" }}>
                {rowProfiles.map(value => {
                    return ProfileContainer(value)
                })}
            </div>
        </div>
    )
}

const Row = props => {
    const {profiles}=props
    let numberOfContainers = Math.floor(useWindowSize()[0] * 0.9 / (290))
    let rowProfiles = profiles.slice(0,numberOfContainers)
    return (
        <div style={{ display: "flex", flexDirection: "row", justifyContent: "space-around", alignItems: "center" }}>
            <div style={{ display: "flex", flexDirection: "row" }}>
                {rowProfiles.map(value => {
                    return ProfileContainer(value)
                })}
            </div>
        </div>
    )
}

const ProfileContainer = (testValue) => {
    return (
        <div style={{ width: "280px", height: "150px", background: "white", margin: "10px" }}>{testValue}</div>
    )
}

const useWindowSize=()=> {
  const [size, setSize] = useState([0, 0]);
  useLayoutEffect(() => {
    const updateSize=()=> {
      setSize([window.innerWidth, window.innerHeight]);
    }
    window.addEventListener('resize', updateSize);
    updateSize();
    return () => window.removeEventListener('resize', updateSize);
  }, []);
  return size;
}

export default Layout
