import React, { useLayoutEffect, useState } from 'react';

const Layout = () => {
    return (
        <div style={{width:"90%", background:"green", margin: "auto", padding:"20px"}}><Row></Row></div>
    )
}

const Row = () => {
    return (
        <div>
            <ShowWindowDimensions></ShowWindowDimensions>
            <ShowWindowDimensions></ShowWindowDimensions>
            <ShowWindowDimensions></ShowWindowDimensions>
        </div>
    )
}

const RenderProfielContainers = () => {
    let windowDimensions = useWindowSize()
    console.log(Math.floor(windowDimensions.width*0.9/(220)))
}

const ProfileContainer = () => {
    return (
        <div style={{width:"200px", height:"150px", background:"white", margin:"20px"}}></div>
    )
}

const useWindowSize=()=> {
  const [size, setSize] = useState([0, 0]);
  useLayoutEffect(() => {
    const updateSize=()=> {
      setSize([window.innerWidth, window.innerHeight]);
    }
    window.addEventListener('resize', updateSize);
    updateSize();
    return () => window.removeEventListener('resize', updateSize);
  }, []);

  return size;
}

const ShowWindowDimensions=(props)=> {
  const [width, height] = useWindowSize();
  return <span>Window size: {width} x {height}, number of </span>;
}

export default Layout
