const ProfileContainer = (testValue) => {
    return (
        <div style={{ width: "200px", height: "150px", background: "white", margin: "20px" }}>{testValue}</div>
    )
}

const useWindowSize=()=> {
  const [size, setSize] = useState([0, 0]);
  useLayoutEffect(() => {
    const updateSize=()=> {
      setSize([window.innerWidth, window.innerHeight]);
    }
    window.addEventListener('resize', updateSize);
    updateSize();
    return () => window.removeEventListener('resize', updateSize);
  }, []);

  return size;
}

